// Parse the LLM response and validate
const llmResponse = $input.first().json.message.content;

try {
  // Parse JSON response from LLM
  const questionData = JSON.parse(llmResponse);

  // Validate required fields
  const requiredFields = ['question', 'visual_element', 'correct_answer', 'explanation'];
  const missingFields = requiredFields.filter(field => !questionData[field]);

  if (missingFields.length > 0) {
    throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
  }

  // Add metadata
  questionData.generated_at = new Date().toISOString();
  questionData.id = `math_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  questionData.subject = 'mathematics';
  questionData.target_age = '4-8 years';

  // Ensure numerical values are numbers
  questionData.left_count = parseInt(questionData.left_count) || 0;
  questionData.right_count = parseInt(questionData.right_count) || 0;
  questionData.correct_answer = parseInt(questionData.correct_answer);
  questionData.difficulty_level = parseInt(questionData.difficulty_level) || 1;

  return [{ json: questionData }];

} catch (error) {
  // Fallback question if LLM fails
  const fallbackQuestion = {
    id: `math_fallback_${Date.now()}`,
    question: "Count the trees! How many do you see?",
    visual_element: "ðŸŒ³",
    left_count: 3,
    right_count: 0,
    correct_answer: 3,
    explanation: "Great job counting! Keep practicing!",
    difficulty_level: 1,
    question_type: "counting",
    subject: "mathematics",
    target_age: "4-8 years",
    generated_at: new Date().toISOString(),
    error: error.message,
    fallback_used: true
  };

  return [{ json: fallbackQuestion }];
}
